INSERT INTO cves (id, cve_id, description, severity, cvss_score, cvss_version, published_date, status, analyst, decision_date, decision_comments, imported_at, last_updated, source) VALUES (24, 'CVE-2026-23525', '1Panel is an open-source, web-based control panel for Linux server management. A stored Cross-Site Scripting (XSS) vulnerability exists in the 1Panel App Store when viewing application details. Malicious scripts can execute in the context of the user’s browser, potentially compromising session data or sensitive system interfaces. All versions of 1Panel up to and including v1.10.33-lts and v2.0.16 are affected. An attacker could publish a malicious application that, when loaded by users (locally or remotely), can execute arbitrary scripts. This may result in theft of user cookies, unauthorized access to system functions, or other actions that compromise the confidentiality, integrity, and availability of the system. The vulnerability is caused by insufficient sanitization of content rendered by the MdEditor component with the `previewOnly` attribute enabled. Specifically, the App Store renders application README content without proper XSS protection, allowing script execution during content rendering; and similar issues exist in system upgrade-related components, which can be fixed by implementing proper XSS sanitization in the MdEditor component. These vulnerabilities can be mitigated by applying proper XSS protection and sanitization when rendering content in the MdEditor component. Safe versions with a patch incorporated are v1.10.34-lts and v2.0.17.', 'MEDIUM', 6.4, '3.1', '2026-01-18T23:15:48.220000Z', 'PENDING', NULL, NULL, NULL, '2026-01-19T00:06:55.809557Z', '2026-01-19T00:06:55.809557Z', 'NVD');
INSERT INTO cves (id, cve_id, description, severity, cvss_score, cvss_version, published_date, status, analyst, decision_date, decision_comments, imported_at, last_updated, source) VALUES (25, 'CVE-2026-23626', 'Kimai is a web-based multi-user time-tracking application. Prior to version 2.46.0, Kimai''s export functionality uses a Twig sandbox with an overly permissive security policy (`DefaultPolicy`) that allows arbitrary method calls on objects available in the template context. An authenticated user with export permissions can deploy a malicious Twig template that extracts sensitive information including environment variables, all user password hashes, serialized session tokens, and CSRF tokens. Version 2.46.0 patches this issue.', 'MEDIUM', 6.8, '3.1', '2026-01-18T23:15:48.393000Z', 'PENDING', NULL, NULL, NULL, '2026-01-19T00:06:55.810653Z', '2026-01-19T00:06:55.810653Z', 'NVD,cveorg');
INSERT INTO cves (id, cve_id, description, severity, cvss_score, cvss_version, published_date, status, analyst, decision_date, decision_comments, imported_at, last_updated, source) VALUES (26, 'CVE-2026-23644', 'esm.sh is a no-build content delivery network (CDN) for web development. Prior to Go pseeudoversion 0.0.0-20260116051925-c62ab83c589e, the software has a path traversal vulnerability due to an incomplete fix. `path.Clean` normalizes a path but does not prevent absolute paths in a malicious tar file. Commit https://github.com/esm-dev/esm.sh/commit/9d77b88c320733ff6689d938d85d246a3af9af16, corresponding to pseudoversion 0.0.0-20260116051925-c62ab83c589e, fixes this issue.', 'HIGH', 7.7, '4.0', '2026-01-18T23:15:48.547000Z', 'PENDING', NULL, NULL, NULL, '2026-01-19T00:06:55.811474Z', '2026-01-19T00:06:55.811474Z', 'NVD,cveorg');
