sourcesContent:
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './Dashboard.css';
import BulletinManagement from './BulletinManagement';
import SourceBadges from './SourceBadges';

// Load Font Awesome
const loadFontAwesome = () => {
  if (!document.querySelector('link[href*="font-awesome"]')) {
    const link = document.createElement('link');
    link.rel = 'stylesheet';
    link.href = 'https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.4.0/css/all.min.css';
    document.head.appendChild(link);
  }
};

// Call it once when component loads
loadFontAwesome();

const modalOverlayStyle = {
  position: 'fixed',
  top: 0,
  left: 0,
  right: 0,
  bottom: 0,
  backgroundColor: 'rgba(0, 0, 0, 0.5)',
  display: 'flex',
  justifyContent: 'center',
  alignItems: 'center',
  zIndex: 1000
};

const modalStyle = {
  backgroundColor: '#fff',
  borderRadius: '8px',
  maxWidth: '600px',
  width: '90%',
  maxHeight: '80vh',
  overflow: 'auto',
  boxShadow: '0 10px 40px rgba(0, 0, 0, 0.3)'
};

const modalHeaderStyle = {
  padding: '20px',
  borderBottom: '1px solid #e2e8f0',
  display: 'flex',
  justifyContent: 'space-between',
  alignItems: 'center'
};

const closeButtonStyle = {
  background: 'none',
  border: 'none',
  fontSize: '24px',
  cursor: 'pointer',
  color: '#64748b'
};

const modalBodyStyle = {
  padding: '20px'
};

const modalSectionStyle = {
  marginBottom: '20px'
};

const modalFooterStyle = {
  padding: '20px',
  borderTop: '1px solid #e2e8f0',
  display: 'flex',
  justifyContent: 'flex-end',
  gap: '10px'
};

function Dashboard({ user, onLogout }) {
  const navigate = useNavigate();
  const [cves, setCves] = useState([]);
  const [stats, setStats] = useState({
    total: 0,
    pending: 0,
    critical: 0,
    high: 0,
    medium: 0,
    low: 0
  });
  const [loading, setLoading] = useState(true);
  const [refreshing, setRefreshing] = useState(false);
  const [activePage, setActivePage] = useState('dashboard');
  const [selectedCve, setSelectedCve] = useState(null);
  const [showModal, setShowModal] = useState(false);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      setLoading(true);
      const response = await fetch('http://localhost:8000/api/cves?limit=200');
      const data = await response.json();
      
      if (data.cves) {
        setCves(data.cves);
        
        // Calculate stats
        const statsData = {
          total: data.cves.length,
          pending: data.cves.filter(c => c.status === 'PENDING').length,
          critical: data.cves.filter(c => c.severity === 'CRITICAL').length,
          high: data.cves.filter(c => c.severity === 'HIGH').length,
          medium: data.cves.filter(c => c.severity === 'MEDIUM').length,
          low: data.cves.filter(c => c.severity === 'LOW').length
        };
        setStats(statsData);
      }
    } catch (error) {
      console.error('Error fetching data:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleRefreshCves = async () => {
    setRefreshing(true);
    try {
      console.log('ðŸ”„ Import rapide depuis NVD (source principale)...');
      
      // âœ… OPTIMISÃ‰: Import rapide depuis NVD uniquement (~2 secondes)
      const importResponse = await fetch('http://localhost:8000/api/cves/import-from-all-sources?days=7&limit=200', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        }
      });
      
      if (!importResponse.ok) {
        throw new Error(`HTTP Error: ${importResponse.status} ${importResponse.statusText}`);
      }
      
      const importData = await importResponse.json();
      console.log('âœ… Import NVD terminÃ©:', importData);
      
      // Recharger les CVEs depuis la base de donnÃ©es
      const response = await fetch('http://localhost:8000/api/cves?limit=200');
      const data = await response.json();
      
      if (data.cves && Array.isArray(data.cves)) {
        setCves(data.cves);
        
        // Recalculer les stats
        const statsData = {
          total: data.cves.length,
          pending: data.cves.filter(c => c.status === 'PENDING').length,
          critical: data.cves.filter(c => c.severity === 'CRITICAL').length,
          high: data.cves.filter(c => c.severity === 'HIGH').length,
          medium: data.cves.filter(c => c.severity === 'MEDIUM').length,
          low: data.cves.filter(c => c.severity === 'LOW').length
        };
        setStats(statsData);
        
        // Message de succÃ¨s dÃ©taillÃ©
        alert(`âœ… Synchronisation NVD rÃ©ussie! (rapide)\n\n` +
              `ðŸ“¥ ImportÃ©s: ${importData.imported || 0}\n` +
              `ðŸ”„ Mis Ã  jour: ${importData.updated || 0}\n` +
              `â­ï¸ IgnorÃ©s: ${importData.skipped || 0} (sans score CVSS)\n\n` +
              `ðŸ“Š CVEs MEDIUM/HIGH affichÃ©s: ${data.cves.length}`);
      } else {
        throw new Error('No CVEs data in response');
      }
    } catch (error) {
      console.error('âŒ Error refreshing CVEs:', error);
      alert(`âŒ Erreur lors de la synchronisation:\n${error.message}`);
    } finally {
      setRefreshing(false);
    }
  };

  const getSeverityBadgeClass = (severity) => {
    switch(severity) {
      case 'CRITICAL': return 'severity-critical';
      case 'HIGH': return 'severity-high';
      case 'MEDIUM': return 'severity-medium';
      case 'LOW': return 'severity-low';
      default: return '';
    }
  };

  const getStatusBadgeClass = (status) => {
    switch(status) {
      case 'PENDING': return 'status-pending';
      case 'ACCEPTED': return 'status-accepted';
      case 'REJECTED': return 'status-rejected';
      case 'DEFERRED': return 'status-deferred';
      default: return '';
    }
  };

  const handleAcceptCve = async (cveId) => {
    try {
      const response = await fetch(`http://localhost:8000/api/cves/test/${cveId}/action`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          action: 'ACCEPTED',
          analyst: user.username,
          comments: 'Approved by analyst'
        })
      });
      if (response.ok) {
        fetchData();
        setShowModal(false);
      } else {
        alert('Error accepting CVE');
      }
    } catch (error) {
      console.error('Error accepting CVE:', error);
    }
  };

  const handleRejectCve = async (cveId) => {
    try {
      const response = await fetch(`http://localhost:8000/api/cves/test/${cveId}/action`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          action: 'REJECTED',
          analyst: user.username,
          comments: 'Rejected by analyst'
        })
      });
      if (response.ok) {
        fetchData();
        setShowModal(false);
      } else {
        alert('Error rejecting CVE');
      }
    } catch (error) {
      console.error('Error rejecting CVE:', error);
    }
  };

  const handleAddToBlacklist = async (cveId) => {
    if (!selectedCve || !selectedCve.affected_products) return;
    
    const product = selectedCve.affected_products[0];
    if (!product) return;
    
    try {
      // Add to blacklist (technologies table)
      const response = await fetch('http://localhost:8000/api/technologies', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          vendor: product.vendor,
          product: product.product,
          status: 'OUT_OF_SCOPE',
          reason: `Blacklisted from CVE ${cveId}`,
          added_by: user.username
        })
      });
      
      if (response.ok) {
        // Also record action in CVE action history
        await fetch(`http://localhost:8000/api/cves/test/${cveId}/action`, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            action: 'DEFERRED',
            analyst: user.username,
            comments: `Added ${product.vendor}/${product.product} to blacklist`
          })
        });
        
        alert(`Added ${product.vendor}/${product.product} to blacklist`);
        fetchData();
        setShowModal(false);
      } else {
        alert('Error adding to blacklist');
      }
    } catch (error) {
      console.error('Error adding to blacklist:', error);
    }
  };

  const openCveModal = (cve) => {
