        )}

        {/* Blacklist Table */}
        <div className="blacklist-section">
          <h2>üìã Produits Blacklist√©s ({blacklistedProducts.length})</h2>

          {loading ? (
            <div className="loading">‚è≥ Chargement...</div>
          ) : blacklistedProducts.length === 0 ? (
            <div className="no-data">Aucun produit blacklist√©</div>
          ) : (
            <div className="blacklist-table-container">
              <table className="blacklist-table">
                <thead>
                  <tr>
                    <th>Vendor</th>
                    <th>Product</th>
                    <th>Reason</th>
                    <th>Ajout√© par</th>
                    <th>Date d'ajout</th>
                    {canEdit && <th>Actions</th>}
                  </tr>
                </thead>
                <tbody>
                  {blacklistedProducts.map((item) => (
                    <tr key={item.id}>
                      <td className="vendor-cell">{item.vendor}</td>
                      <td className="product-cell">{item.product}</td>
                      <td className="reason-cell">{item.reason || '-'}</td>
                      <td className="added-by-cell">{item.added_by}</td>
                      <td className="date-cell">
                        {new Date(item.created_at).toLocaleDateString('fr-FR')}
                      </td>
                      {canEdit && (
                        <td className="actions-cell">
                          <button
                            className="btn btn-danger btn-small"
                            onClick={() => handleRemoveFromBlacklist(item.id)}
                          >
                            ‚ôªÔ∏è R√©int√©grer
                          </button>
                        </td>
                      )}
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default BlacklistManagement;
