import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './AnalystKPIDashboard.css';

function AnalystKPIDashboard({ user, onLogout }) {
  const [performance, setPerformance] = useState([]);
  const [statistics, setStatistics] = useState(null);
  const [trends, setTrends] = useState([]);
  const [period, setPeriod] = useState('month');
  const [trendDays, setTrendDays] = useState(30);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchKPIData();
  }, [period, trendDays]);

  const fetchKPIData = async () => {
    try {
      setLoading(true);

      // Fetch analyst performance
      const perfResponse = await fetch(`http://localhost:8000/api/kpi/analyst-performance?period=${period}`, {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });

      // Fetch overall statistics
      const statsResponse = await fetch(`http://localhost:8000/api/kpi/action-statistics?period=${period}`, {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });

      // Fetch trends
      const trendsResponse = await fetch(`http://localhost:8000/api/kpi/action-trends?days=${trendDays}`, {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });

      if (perfResponse.ok) {
        const perfData = await perfResponse.json();
        setPerformance(perfData.analysts || []);
      }

      if (statsResponse.ok) {
        const statsData = await statsResponse.json();
        setStatistics(statsData);
      }

      if (trendsResponse.ok) {
        const trendsData = await trendsResponse.json();
        setTrends(trendsData.trends || []);
      }

      setLoading(false);
    } catch (error) {
      console.error('Error fetching KPI data:', error);
      setLoading(false);
    }
  };

  const getSeverityColor = (severity) => {
    const colors = {
      CRITICAL: '#dc3545',
      HIGH: '#fd7e14',
      MEDIUM: '#ffc107',
      LOW: '#28a745'
    };
    return colors[severity] || '#6c757d';
  };

  return (
    <div className="dashboard-container">
      {/* Sidebar */}
      <div className="sidebar">
        <div className="sidebar-header">
          <div className="logo">
            <div className="logo-icon">üìä</div>
            <div>CTBA</div>
          </div>
        </div>

        <div className="nav-menu">
          <Link to="/" className="nav-item">
            üìä Dashboard
          </Link>
          <Link to="/accepted" className="nav-item">
            ‚úÖ CVEs Accept√©s
          </Link>
          <Link to="/rejected" className="nav-item">
            ‚ùå CVEs Rejet√©s
          </Link>
          <Link to="/ingestion" className="nav-item">
            üì° Ingestion Sources
          </Link>
          <Link to="/blacklist" className="nav-item">
            üö´ Produits Blacklist√©s
          </Link>
          <Link to="/history" className="nav-item">
            üìú Historique des Actions
          </Link>
          <Link to="/kpi" className="nav-item active">
            üìà Rapports & KPIs
          </Link>
        </div>

        <div className="sidebar-footer">
          <p>CTBA Platform v7.0.0</p>
          <p>¬© 2026 Tds by Nomios. All rights reserved.</p>
        </div>
      </div>

      {/* Main Content */}
      <div className="main-content">
        {/* Top Bar */}
        <div className="top-bar">
          <div className="page-title">
            <h1>üìà Rapports & KPIs des Analystes</h1>
            <p>Suivi de la performance et des m√©triques d'analyse</p>
          </div>
          <div className="user-section">
            <span className="user-info">üë§ {user.username} ({user.role})</span>
            <button onClick={onLogout} className="btn-logout">üîì Logout</button>
          </div>
        </div>

        {/* Controls */}
        <div className="kpi-controls">
          <div className="control-group">
            <label>P√©riode d'analyse:</label>
            <select value={period} onChange={(e) => setPeriod(e.target.value)} className="control-select">
              <option value="day">Aujourd'hui</option>
              <option value="week">Cette semaine</option>
              <option value="month">Ce mois</option>
              <option value="year">Cette ann√©e</option>
              <option value="all">Toute l'ann√©e</option>
            </select>
          </div>
          <div className="control-group">
            <label>Jours pour tendances:</label>
            <select value={trendDays} onChange={(e) => setTrendDays(parseInt(e.target.value))} className="control-select">
              <option value="7">7 jours</option>
              <option value="14">14 jours</option>
              <option value="30">30 jours</option>
              <option value="90">90 jours</option>
            </select>
          </div>
        </div>

        {loading ? (
          <div style={{ padding: '40px', textAlign: 'center' }}>‚è≥ Chargement...</div>
        ) : (
          <>
            {/* Overall Statistics */}
            {statistics && (
              <div className="kpi-section">
                <h2>üìä Statistiques Globales</h2>
                <div className="stats-grid">
                  <div className="stat-card">
                    <div className="stat-label">Total Examin√©s</div>
                    <div className="stat-value">{statistics.total_reviewed}</div>
                    <div className="stat-subtext">CVEs</div>
                  </div>
                  <div className="stat-card">
                    <div className="stat-label">Accept√©s</div>
                    <div className="stat-value" style={{ color: '#28a745' }}>
                      {statistics.total_accepted}
                    </div>
                    <div className="stat-subtext">{statistics.acceptance_rate}%</div>
                  </div>
                  <div className="stat-card">
                    <div className="stat-label">Rejet√©s</div>
                    <div className="stat-value" style={{ color: '#dc3545' }}>
                      {statistics.total_rejected}
                    </div>
                    <div className="stat-subtext">
                      {statistics.total_reviewed > 0 
                        ? Math.round((statistics.total_rejected / statistics.total_reviewed) * 100) 
                        : 0}%
                    </div>
                  </div>
                  <div className="stat-card">
                    <div className="stat-label">Diff√©r√©s</div>
                    <div className="stat-value" style={{ color: '#ffc107' }}>
                      {statistics.total_deferred}
                    </div>
                    <div className="stat-subtext">
                      {statistics.total_reviewed > 0 
                        ? Math.round((statistics.total_deferred / statistics.total_reviewed) * 100) 
                        : 0}%
                    </div>
                  </div>
                </div>

                {/* Severity Distribution */}
                <div className="severity-section">
                  <h3>Distribution par S√©v√©rit√©</h3>
                  <div className="severity-bars">
                    {Object.entries(statistics.severity_distribution || {}).map(([severity, count]) => (
                      <div key={severity} className="severity-bar-item">
                        <div className="severity-bar-label">{severity}</div>
                        <div className="severity-bar-chart">
                          <div
                            className="severity-bar-fill"
                            style={{
                              width: `${(count / Math.max(...Object.values(statistics.severity_distribution || {}))) * 100}%`,
                              backgroundColor: getSeverityColor(severity)
                            }}
                          >
                            {count}
                          </div>
                        </div>
                      </div>
                    ))}
                  </div>
                </div>
              </div>
            )}

            {/* Analyst Performance */}
            <div className="kpi-section">
              <h2>üë• Performance des Analystes</h2>
              {performance.length === 0 ? (
                <div style={{ padding: '20px', textAlign: 'center', color: '#666' }}>
                  Aucune donn√©e disponible pour cette p√©riode
                </div>
              ) : (
                <table className="kpi-table">
                  <thead>
                    <tr>
                      <th>Analyste</th>
                      <th>Actions Totales</th>
                      <th>Accept√©s</th>
                      <th>Rejet√©s</th>
                      <th>Diff√©r√©s</th>
                      <th>Taux Acceptation</th>
                      <th>Moy. Actions/Jour</th>
                      <th>Derni√®re Action</th>
                    </tr>
                  </thead>
                  <tbody>
                    {performance.map((analyst) => (
                      <tr key={analyst.analyst}>
                        <td className="analyst-name">üë§ {analyst.analyst}</td>
                        <td className="metric-value">{analyst.total_actions}</td>
                        <td style={{ color: '#28a745' }}>{analyst.accepted}</td>
                        <td style={{ color: '#dc3545' }}>{analyst.rejected}</td>
                        <td style={{ color: '#ffc107' }}>{analyst.deferred}</td>
                        <td>
                          <div className="progress-bar">
                            <div
                              className="progress-fill"
                              style={{
                                width: `${analyst.acceptance_rate}%`,
                                backgroundColor: analyst.acceptance_rate > 70 ? '#28a745' : analyst.acceptance_rate > 40 ? '#ffc107' : '#dc3545'
                              }}
                            >
                              {analyst.acceptance_rate}%
                            </div>
                          </div>
                        </td>
                        <td className="metric-value">{analyst.avg_actions_per_day}</td>
                        <td className="date-cell">
                          {analyst.last_action_date 
                            ? new Date(analyst.last_action_date).toLocaleDateString('fr-FR')
                            : '-'
                          }
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              )}
            </div>

            {/* Trends */}
            <div className="kpi-section">
              <h2>üìà Tendances ({trendDays} jours)</h2>
              {trends.length === 0 ? (
                <div style={{ padding: '20px', textAlign: 'center', color: '#666' }}>
                  Aucune donn√©e de tendance disponible
                </div>
              ) : (
                <table className="kpi-table">
                  <thead>
                    <tr>
                      <th>Date</th>
                      <th>Accept√©s</th>
                      <th>Rejet√©s</th>
                      <th>Diff√©r√©s</th>
                      <th>Total du Jour</th>
                    </tr>
                  </thead>
                  <tbody>
                    {trends.map((trend) => (
                      <tr key={trend.date}>
                        <td className="date-cell">{new Date(trend.date).toLocaleDateString('fr-FR')}</td>
                        <td style={{ color: '#28a745' }}>{trend.ACCEPTED}</td>
                        <td style={{ color: '#dc3545' }}>{trend.REJECTED}</td>
                        <td style={{ color: '#ffc107' }}>{trend.DEFERRED}</td>
                        <td className="metric-value" style={{ fontWeight: 'bold' }}>{trend.total}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              )}
            </div>
          </>
        )}
      </div>
    </div>
  );
}

export default AnalystKPIDashboard;
