import React, { useState } from 'react';
import './Login.css';

function Login({ onLoginSuccess }) {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('VOC_L1');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      const response = await fetch('http://localhost:8000/api/auth/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          username: username.trim(),
          password: password.trim()
        })
      });

      const data = await response.json();

      if (!response.ok) {
        setError(data.detail || 'Login failed');
        setLoading(false);
        return;
      }

      // Store token and user info
      localStorage.setItem('token', data.access_token);
      localStorage.setItem('user', JSON.stringify({
        username: data.username,
        role: data.role
      }));

      // Notify parent component
      onLoginSuccess({
        username: data.username,
        role: data.role,
        token: data.access_token
      });
    } catch (err) {
      setError('Connection error: ' + err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="login-container">
      <div className="login-card">
        <div className="login-header">
          <div className="login-logo">‚ö†Ô∏è</div>
          <h1>CTBA Platform</h1>
          <p>CVE Triage & Bulletin Automation</p>
        </div>

        <form onSubmit={handleSubmit} className="login-form">
          <div className="form-group">
            <label htmlFor="username">Username</label>
            <input
              id="username"
              type="text"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              placeholder="analyst@ctba.local"
              disabled={loading}
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="password">Password</label>
            <input
              id="password"
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢"
              disabled={loading}
              required
            />
          </div>

          {error && <div className="error-message">{error}</div>}

          <button 
            type="submit" 
            className="btn-login"
            disabled={loading || !username || !password}
          >
            {loading ? '‚è≥ Logging in...' : 'üîì Login'}
          </button>
        </form>

        <div className="login-footer">
          <h3>Test Credentials</h3>
          <table className="credentials-table">
            <thead>
              <tr>
                <th>Username</th>
                <th>Password</th>
                <th>Role</th>
              </tr>
            </thead>
            <tbody>
              <tr>
                <td>analyst1</td>
                <td>password123</td>
                <td>VOC_L1 Analyst</td>
              </tr>
              <tr>
                <td>lead1</td>
                <td>password123</td>
                <td>VOC_LEAD</td>
              </tr>
              <tr>
                <td>admin</td>
                <td>password123</td>
                <td>ADMINISTRATOR</td>
              </tr>
              <tr>
                <td>manager1</td>
                <td>password123</td>
                <td>MANAGER</td>
              </tr>
            </tbody>
          </table>
        </div>

        <div className="login-info">
          <p>¬© 2026 Tds by Nomios. All rights reserved.</p>
          <p>CTBA Platform v7.0.0</p>
        </div>
      </div>
    </div>
  );
}

export default Login;
