import React, { useState, useEffect } from 'react';
import './BlacklistManagement.css';

function BlacklistManagement({ user, onLogout }) {
  const [blacklistedProducts, setBlacklistedProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [vendor, setVendor] = useState('');
  const [product, setProduct] = useState('');
  const [reason, setReason] = useState('');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [showScoreModal, setShowScoreModal] = useState(false);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [scoreAdjustments, setScoreAdjustments] = useState([]);
  const [adjustmentScore, setAdjustmentScore] = useState('');
  const [adjustmentReason, setAdjustmentReason] = useState('');

  useEffect(() => {
    fetchBlacklist();
  }, []);

  const fetchBlacklist = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(
        'http://localhost:8000/api/technologies?status=OUT_OF_SCOPE',
        {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        }
      );

      if (response.ok) {
        const data = await response.json();
        // Extract technologies from response
        setBlacklistedProducts(data.technologies || []);
      }
      setLoading(false);
    } catch (error) {
      console.error('Error fetching blacklist:', error);
      setLoading(false);
      setError('Erreur lors du chargement de la blacklist');
    }
  };

  const handleAddToBlacklist = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');

    if (!vendor.trim() || !product.trim()) {
      setError('Veuillez remplir tous les champs obligatoires');
      return;
    }

    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:8000/api/technologies', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({
          vendor: vendor.trim(),
          product: product.trim(),
          status: 'OUT_OF_SCOPE',
          reason: reason.trim()
        })
      });

      if (response.ok) {
        setSuccess(`‚úÖ ${vendor}/${product} ajout√© √† la blacklist`);
        setVendor('');
        setProduct('');
        setReason('');
        fetchBlacklist();
      } else {
        const data = await response.json();
        setError(data.detail || 'Erreur lors de l\'ajout √† la blacklist');
      }
    } catch (error) {
      console.error('Error adding to blacklist:', error);
      setError('Erreur de connexion');
    }
  };

  const handleRemoveFromBlacklist = async (id) => {
    if (!window.confirm('√ätes-vous s√ªr de vouloir r√©int√©grer ce produit?')) {
      return;
    }

    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:8000/api/technologies/${id}`, {
        method: 'DELETE',
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (response.ok) {
        setSuccess('‚úÖ Produit r√©int√©gr√© avec succ√®s');
        fetchBlacklist();
      } else {
        setError('Erreur lors de la suppression');
      }
    } catch (error) {
      console.error('Error removing from blacklist:', error);
      setError('Erreur de connexion');
    }
  };

  const openScoreModal = async (item) => {
    setSelectedProduct(item);
    setShowScoreModal(true);
    setAdjustmentScore('');
    setAdjustmentReason('');
    
    // Fetch existing adjustments for this product
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(
        `http://localhost:8000/api/cvss-adjustments?vendor=${encodeURIComponent(item.vendor)}&product=${encodeURIComponent(item.product)}`,
        {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        }
      );
      
      if (response.ok) {
        const data = await response.json();
        setScoreAdjustments(data.adjustments || []);
      }
    } catch (error) {
      console.error('Error fetching adjustments:', error);
    }
  };

  const handleSaveScoreAdjustment = async (e) => {
    e.preventDefault();
    
    if (!selectedProduct || !adjustmentScore) {
      setError('Veuillez entrer un score d\'ajustement');
      return;
    }

    const score = parseFloat(adjustmentScore);
    if (isNaN(score) || score < 0 || score > 10) {
      setError('Le score doit √™tre entre 0 et 10');
      return;
    }

    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:8000/api/cvss-adjustments', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/x-www-form-urlencoded',
          'Authorization': `Bearer ${token}`
        },
        body: new URLSearchParams({
          cve_id: 'multiple',
          vendor: selectedProduct.vendor,
          product: selectedProduct.product,
          adjusted_score: score,
          adjustment_reason: adjustmentReason,
          analyst: user.username
        })
      });

      if (response.ok) {
        setSuccess('‚úÖ Ajustement de score enregistr√©');
        setShowScoreModal(false);
        setSelectedProduct(null);
        setAdjustmentScore('');
        setAdjustmentReason('');
      } else {
        const data = await response.json();
        setError(data.detail || 'Erreur lors de l\'enregistrement');
      }
    } catch (error) {
      console.error('Error saving adjustment:', error);
      setError('Erreur de connexion');
    }
  };

  const canEdit = user.role === 'ADMINISTRATOR' || user.role === 'VOC_L1';

  return (
    <div className="dashboard-container">
      {/* Sidebar */}
      <div className="sidebar">
        <div className="sidebar-header">
          <div className="logo">
            <div className="logo-icon">‚ö†Ô∏è</div>
            <span>CTBA</span>
          </div>
        </div>

        <div className="nav-menu">
          <a href="/" className="nav-item">
            üìä Dashboard
          </a>
          <a href="/accepted" className="nav-item">
            ‚úÖ CVEs Accept√©s
          </a>
          <a href="/rejected" className="nav-item">
            ‚ùå CVEs Rejet√©s
          </a>
          <a href="/blacklist" className="nav-item active">
            üö´ Produits Blacklist√©s
          </a>
          <a href="/history" className="nav-item">
            üìú Historique des Actions
          </a>
        </div>

        <div className="sidebar-footer">
          <p>CTBA Platform v7.0.0</p>
          <p>¬© 2026 Tds by Nomios. All rights reserved.</p>
        </div>
      </div>

      {/* Main Content */}
      <div className="main-content">
        {/* Top Bar */}
        <div className="top-bar">
          <div className="page-title">
            <h1>üö´ Blacklist Management</h1>
            <p>Gestion des produits blacklist√©s et r√©int√©gration</p>
          </div>
          <div className="user-section">
            <span className="user-info">üë§ {user.username} ({user.role})</span>
            <button onClick={onLogout} className="btn-logout">üîì Logout</button>
          </div>
        </div>

        {/* Add to Blacklist Form */}
        {canEdit && (
          <div className="add-blacklist-form">
            <h2>‚ûï Ajouter √† la blacklist</h2>
            
            {error && <div className="alert alert-error">{error}</div>}
            {success && <div className="alert alert-success">{success}</div>}

            <form onSubmit={handleAddToBlacklist}>
              <div className="form-group">
                <label>Vendor *</label>
                <input
                  type="text"
                  value={vendor}
                  onChange={(e) => setVendor(e.target.value)}
                  placeholder="e.g., Apache"
                  required
                />
              </div>

              <div className="form-group">
                <label>Product *</label>
                <input
                  type="text"
                  value={product}
                  onChange={(e) => setProduct(e.target.value)}
                  placeholder="e.g., Apache HTTP Server"
                  required
                />
              </div>

              <div className="form-group">
                <label>Reason</label>
                <textarea
                  value={reason}
                  onChange={(e) => setReason(e.target.value)}
                  placeholder="Raison de l'ajout √† la blacklist..."
                  rows="3"
                />
              </div>

              <button type="submit" className="btn btn-primary">
                üö´ Add to Blacklist
              </button>
            </form>
          </div>
        )}

        {/* Blacklist Table */}
        <div className="blacklist-section">
          <h2>üìã Produits Blacklist√©s ({blacklistedProducts.length})</h2>

          {loading ? (
            <div className="loading">‚è≥ Chargement...</div>
          ) : blacklistedProducts.length === 0 ? (
            <div className="no-data">Aucun produit blacklist√©</div>
          ) : (
            <div className="blacklist-table-container">
              <table className="blacklist-table">
                <thead>
                  <tr>
                    <th>Vendor</th>
                    <th>Product</th>
                    <th>Reason</th>
                    <th>Ajout√© par</th>
                    <th>Date d'ajout</th>
                    {canEdit && <th>Actions</th>}
                  </tr>
                </thead>
                <tbody>
                  {blacklistedProducts.map((item) => (
                    <tr key={item.id}>
                      <td className="vendor-cell">{item.vendor}</td>
                      <td className="product-cell">{item.product}</td>
                      <td className="reason-cell">{item.reason || '-'}</td>
                      <td className="added-by-cell">{item.added_by}</td>
                      <td className="date-cell">
                        {new Date(item.created_at).toLocaleDateString('fr-FR')}
                      </td>
                      {canEdit && (
                        <td className="actions-cell">
                          <button
                            className="btn btn-warning btn-small"
                            onClick={() => openScoreModal(item)}
                            style={{marginRight: '5px'}}
                          >
                            üìä Corriger Score
                          </button>
                          <button
                            className="btn btn-danger btn-small"
                            onClick={() => handleRemoveFromBlacklist(item.id)}
                          >
                            ‚ôªÔ∏è R√©int√©grer
                          </button>
                        </td>
                      )}
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default BlacklistManagement;
